$(document).ready(function(){"use strict";$(".navigation button").on("click",function(){var t=$(this).data("scroll");$("html,body").animate({scrollTop:$(""+t).offset().top},2e3)}),$(".portfolio-item__image").on("click",function(){$(this).siblings(".portfolio-item__list").toggleClass("visually-hidden")}),$(".header__menu-button").on("click",function(){$(".navigation--header").toggleClass("active")});var t=$(".portfolio-slider");t.slick({autoplay:!0,autoplaySpeed:5e3,arrows:!0,dots:!1,initialSlide:1}),t.on("beforeChange",function(){var t=$(".portfolio-slider .slick-current").data("slide");$('.dots-list[data-slide-dots="'+t+'"]').removeClass("active")}),t.on("afterChange",function(){var t=$(".portfolio-slider .slick-current").data("slide");$('.dots-list[data-slide-dots="'+t+'"]').addClass("active")}),$(".dot-item").on("click",function(){var t=$(this).data("dot"),i=$('.dot-content-item[data-dot-content="'+t+'"]');i.siblings().slideUp(),i.slideToggle()}),$(".tab-item__title").on("click",function(){var t=$(this).parent();t.addClass("active"),t.siblings().removeClass("active")}),$(".section-team-slider").slick({autoplay:!0,autoplaySpeed:1e3,arrows:!1,dots:!1,fade:!0})});