$(document).ready(function(){"use strict";$(".navigation button").on("click",function(){var o=$(this).data("scroll");$("html,body").animate({scrollTop:$(""+o).offset().top},1e3)}),$(".portfolio-item__image").on("click",function(){$(this).siblings(".portfolio-item__list").slideToggle()}),$(".header__menu-button").on("click",function(){$(".navigation--header").slideToggle()})});